import React from "react";
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';
import Input from '@material-ui/core/Input';
import { bool } from "prop-types";

class Table extends React.Component{

    state = { 
        name : "",
        dob:"2018-12-31",
        email:"",
        number:""
    };
    
    handleChange = (e) =>{
        console.log("old state", this.state);
        const field = e.target.placeholder;
        const newText = e.target.value;
        console.log(newText);


        switch(field){
            
            case 'Name': {
                //console.log("name");

                this.setState({...this.state, name: newText});
                break;         
            }
            case 'Date':{
                //console.log("date");
                this.setState({...this.state, dob: newText});
                break;
            }
            case 'Email':{
                this.setState({...this.state, email: newText});
                break;       
            }
            case 'Number':{
                this.setState({...this.state, number: newText});
                break;          
            }
            default: this.state;
            break;
        }
        
        console.log("new mini state", this.state);
    };

    render(){
        const read = false; //sets readOnly property 
        const {client} = this.props;
        

        const styles = {
            margin: '1em 1em 1em 1em'
        }
        const styles2 = {
            margin: '1em 8em 1em 1em',
        }
        return(
            <div id="formWrapper">
                  <Input required disableUnderline={read} inputProps={{ readOnly: read ? true:false}} label="Name" defaultValue= {client.name} onChange={this.handleChange} margin="dense" style={styles}/>
                  <Input required disableUnderline={read} inputProps={{ readOnly: read ? true:false}} label="Name" defaultValue= {client.number} onChange={this.handleChange} margin="dense" style={styles}/>
                  <Input required disableUnderline={read} inputProps={{ readOnly: read ? true:false}} label="Name" defaultValue= {client.email} onChange={this.handleChange} margin="dense" style={styles}/>
                  <Input required disableUnderline={read} inputProps={{ readOnly: read ? true:false}} label="Name" defaultValue= {client.dob} onChange={this.handleChange} margin="dense" style={styles2}/>
            </div>
        )
    }
}
/*
               
*/

export default Table;
