import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';

class Form extends React.Component{

    state = {
        text : ''
    };
    
    handleChange = (e) =>{
        const newText = e.target.value;
        this.setState({
            text : newText
        });

    };
    handleKeyDown = (e) => {

    }
    render(){
        const styles = {
            marginLeft: '1em', marginRight:'1em'
        }
        return(
            <div id="formWrapper">
            <TextField label="Required" onChange={this.handleChange} onKeyDown placeholder="Name" margin="normal" style={styles}/>
            <TextField label="Required" defaultValue="2018-12-31" label="Date of Birth" type="date" margin="normal" style={styles}/>
            <TextField label="Required" placeholder="Number" margin="normal" style={styles}/>
            <TextField label="Required" placeholder="Email" margin="normal" style={styles}/>
            <div style={{width: '100%',display:"flex", justifyContent:'flex-end'}}>
            <Button color="primary" aria-label="Add" >
        <       AddIcon />
            </Button>
            </div>
            </div>
        )
    }
}
