import React, { Component } from 'react';
//import logo from './logo.svg';
import './App.css';
import gql from "graphql-tag";
import { graphql } from 'react-apollo';
import Paper from '@material-ui/core/Paper';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemSecondaryAction from '@material-ui/core/ListItemSecondaryAction';
import ListItemText from '@material-ui/core/ListItemText';
import Checkbox from '@material-ui/core/Checkbox';
import IconButton from '@material-ui/core/IconButton';
import CommentIcon from '@material-ui/icons/Comment';
//import CheckboxList from "./Compnents/CheckboxList";

const TodoQuery = gql`
{
  todos{
    id
    complete
    text
  }
}
`;


class App extends Component {

  state = {
    checked: [0],
  };

  handleToggle = value => () => {
    const { checked } = this.state;
    const currentIndex = checked.indexOf(value);
    const newChecked = [...checked];

    if (currentIndex === -1) {
      newChecked.push(value);
    } else {
      newChecked.splice(currentIndex, 1);
    }

    this.setState({
      checked: newChecked,
    });
  };

  render() {
    console.log(this.props);
    const {data:{loading, todos}} = this.props;

    if (loading){
      return null;
    }
    return (
      <div style={{display: 'flex', justifyContent: 'center'}}>
        <div style = {{width: '50%'}}>

            <List style = {{fontSize:'53em'}}>
            {todos.map(todo => (
            <ListItem  key={todo.id} role={undefined} dense button onClick={this.handleToggle(todo)}>
              <Checkbox
                checked={todo.complete}
                tabIndex={-1}
                disableRipple
              />
              <ListItemText style={{display: 'flex', justifyContent: 'center'}} primary={todo.text} />
              <ListItemSecondaryAction>
                <IconButton aria-label="Comments">
                  <CommentIcon />
                </IconButton>
              </ListItemSecondaryAction>
            </ListItem>
              ))}
            </List>


         </div>       
       </div>
    )
  }
}



export default graphql(TodoQuery)(App);
