import React, { Component } from 'react';
import './App.css';
import gql from "graphql-tag";
import { graphql,compose } from 'react-apollo';
import Paper from '@material-ui/core/Paper';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import Form from "./Form";
import Table from "./Table";
import { auto } from 'async';
import { connect } from 'react-redux';
import Divider from '@material-ui/core/Divider';

const ClientQuery = gql`
{
  clients {
    name
    number
    dob
    email
    id
  }
}
`;
const editMutation = gql`
mutation($id:ID!, $name: String!, $number: String!, $email : String!, $dob : String!){
  editText(id:$id,name:$name, number:$number, email:$email, dob:$dob)
}
`;


const RemoveMutation = gql`
mutation($id:ID!){
	removeClient(id:$id)
}
`;

const CreateMutation  = gql`
mutation($name: String!, $number: String!, $email : String!, $dob : String!){
  createClient(name:$name, number:$number, email:$email, dob:$dob){
    name,
    number,
    email,
    dob,
    id
  }
}
`;
const loaded = false;

class App extends Component {

  constructor(props){
    super(props);
    //console.log("IN STATE",this.props.loadingdsa);
    this.state={
      editable:false
    }
  };

  createClient = async ({name, number, email, dob}) =>{

    //edit the client.

    await this.props.createClient({
      variables:{
        name,
        number,
        email,
        dob
      }, refetchQueries: [{
        query: ClientQuery
      }]
    });


    console.log("CREATE CLIENT ",this.props.data);
      //this.props.dispatch({type:"ADD_CLIENT", payload: ???? no id to send to REDUX store }); 
  };


  editText = async ({client,name2, number2, email2, dob2}) => {
    //update client
    console.log("EDITING?",client);
    //this.setState({editable : !this.state.editable}); //ACTION TO EDIT
    await this.props.editText({
      variables:{
        id : client.id,
        name: name2,    
        number: number2,
        email: email2,
        dob: dob2
      }, refetchQueries: [{
        query: ClientQuery
      }]
    });

  };

  removeClient = async client =>{  

    console.log("REMOVED",client);
    //this.props.dispatch({type:"REMOVE_CLIENT", payload: client}); 

      await this.props.removeClient({
        variables:{
          id : client.id      
        }, refetchQueries: [{
          query: ClientQuery
        }]
      });
  };

  render() {

    const {data:{loading, clients}} = this.props; //change to get from store?

    if (loading){
      return null;
    } 

    return (


      <div style={{display:'flex'}}> 
        <div style={{margin:"auto", width: auto}}>

            <h1 style={{ display: 'flex', justifyContent: 'center',margin: '1em 0 0 0' }}>Clients </h1> 
            <Paper style={{margin: '3em 0 0 0'}}>
            <List>
            {this.props.data.clients.map(client => (
                <div>
                <ListItem key={client.id} role={undefined} style={{margin: '1em 1em 1em 1em'}} dense>                  
                    <Table client = {client} removeClient={this.removeClient} editText={this.editText}></Table>
                </ListItem> 
                <Divider key={client.id} />
                </div>           
              ))}
            </List> 
            <p style={{margin: '1em 1em 1em 1em'}}>Add New Client</p>

            <Form clients = {clients} createClient={this.createClient} ></Form>             
            </Paper>
        </div>
      </div>

    )
  }
}


const mapStateToProps = state =>({
 client: state.client
})



export default compose(
graphql(editMutation, {name:"editText"}),
graphql(CreateMutation, {name:"createClient"}),
graphql(RemoveMutation, {name:"removeClient"}),
graphql(ClientQuery))(connect(mapStateToProps)(App));
