import React, { Component } from 'react';
import './App.css';
import gql from "graphql-tag";
import { graphql,compose } from 'react-apollo';
import Paper from '@material-ui/core/Paper';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemSecondaryAction from '@material-ui/core/ListItemSecondaryAction';
import ListItemText from '@material-ui/core/ListItemText';
import Checkbox from '@material-ui/core/Checkbox';
import IconButton from '@material-ui/core/IconButton';
import EditIcon from '@material-ui/icons/Edit';
import DeleteIcon from '@material-ui/icons/Delete';
import Form from "./Form";
import { auto } from 'async';
import Input from '@material-ui/core/Input';

const ClientQuery = gql`
{
  clients {
    name
    number
    dob
    email
    id
  }
}
`;

const editMutation = gql`
mutation($id:ID!, $name: String!, $number: String!, $email : String!, $dob : String!){
  editText(id:$id,name:$name, number:$number, email:$email,dob:$dob)
}
`;


const RemoveMutation = gql`
mutation($id:ID!){
	removeClient(id:$id)
}
`;


class App extends Component {
/*
  updateClient = async client => {
    //update client
    console.log(client);
    await this.props.updateClient({
      variables:{
        id : client.id,
        complete: !client.complete
      },
    });
  };*/
/*
  editMutation = async client => {
    //update client
    console.log(client);
    await this.props.editMutation({
      variables:{
        id : client.id,
        complete: !client.complete
      },
    });
  };
*/
  removeClient = async client =>{   
      await this.props.removeClient({
        variables:{
          id : client.id      
        }
      });
  };

  editClient = async client =>{
    //edit the client.
  };

  render() {

    const {data:{loading, clients}} = this.props;
    const addClient = false;
    const readOnly = false;
    //{addClient ? <Form clients = {clients}></Form> : null}
    if (loading){
      return null;
    } 
    return (
      <div style={{display:'flex'}}> 
        <div style={{margin:"auto", width: auto}}>
          <Paper>

            <List>
              {clients.map(client => (
                <ListItem key={`number ${client.id}`} role={undefined} dense>

                  <Input required inputProps={{ readOnly: read ? true:false}} label="Name" placeholder= {client.name} onChange={this.handleChange} margin="dense" style={styles}/>
                  <ListItemText primary={client.name == null ? "Empty?": `${client.name} ${client.number} ${client.email} ${client.dob}`} />

                  <ListItemSecondaryAction>
                    <IconButton >
                      <EditIcon onClick ={()=> this.editClient(client)}/>
                      </IconButton>
                      <IconButton >
                      <DeleteIcon onClick ={()=> this.removeClient(client)}/>
                    </IconButton>

                  </ListItemSecondaryAction>
                </ListItem>   
              ))}
            </List>
            <Form clients = {clients}></Form>             
          </Paper>
        </div>
      </div>
    )
  }
}


export default compose(
graphql(editMutation, {name:"editText"}),
graphql(RemoveMutation, {name:"removeClient"}),
graphql(ClientQuery))
(App);
