import { createStore} from 'redux'

const initialState = {
    clients: []
};

//const {data:{loading, clients}} = this.props;
//console.log(clients)
function reducer(state=initialState, action){
    console.log("ACTION IS ",action);
    switch (action.type){

        case "ADD_CLIENT": {
            console.log("STATE IS", state);
            return {
                clients: [...state.clients,
                {
                    name:"HARDCODED ID IN STATE, DIF IN SERVER",
                    email:"HARDCODED ID IN STATE, DIF IN SERVER",
                    dob:"HARDCODED ID IN STATE, DIF IN SERVER",
                    number:"HARDCODED ID IN STATE, DIF IN SERVER",
                    id:"HARDCODED ID IN STATE, DIF IN SERVER"
                }
                ]
            }
        }
        case "REMOVE_CLIENT": {
            console.log("STATE IS", state);
            return state
        }
        case "EDIT_CLIENT" : {
            console.log("STATE IS", state);
            return state
        }
        default:
            return state;

    }
    
}

const store = createStore(reducer);

export default store;
