import React, { Component } from 'react';
import './App.css';
import gql from "graphql-tag";
import { graphql,compose } from 'react-apollo';
import Paper from '@material-ui/core/Paper';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemSecondaryAction from '@material-ui/core/ListItemSecondaryAction';
import ListItemText from '@material-ui/core/ListItemText';
import Checkbox from '@material-ui/core/Checkbox';
import IconButton from '@material-ui/core/IconButton';
import EditIcon from '@material-ui/icons/Edit';
import DeleteIcon from '@material-ui/icons/Delete';
import Form from "./Form";
import Table from "./Table";
import { auto } from 'async';
import Input from '@material-ui/core/Input';

const ClientQuery = gql`
{
  clients {
    name
    number
    dob
    email
    id
  }
}
`;

const editMutation = gql`
mutation($id:ID!, $name: String!, $number: String!, $email : String!, $dob : String!){
  editText(id:$id,name:$name, number:$number, email:$email,dob:$dob)
}
`;


const RemoveMutation = gql`
mutation($id:ID!){
	removeClient(id:$id)
}
`;

const CreateMutation  = gql`
mutation($name: String!, $number: String!, $email : String!, $dob : String!){
  createClient(name:$name, number:$number, email:$email, dob:$dob)
}
`;

const read = false;

class App extends Component {
/*
  updateClient = async client => {
    //update client
    console.log(client);
    await this.props.updateClient({
      variables:{
        id : client.id,
        complete: !client.complete
      },
    });
  };*/

  createClient = async ({name, number, email, dob}) =>{
    //edit the client.
    await this.props.createClient({
      variables:{
        name,
        number,
        email,
        dob
      }


    })
    //console.log(client);
  };
/*
  editMutation = async client => {
    //update client
    console.log(client);
    await this.props.editMutation({
      variables:{
        id : client.id,
        name: client.name,    can't use client.name or it'll be the same?
        number: client.number,
        email: client.email,
        dob: client.dob
      },
    });
  };
*/
  removeClient = async client =>{   
      await this.props.removeClient({
        variables:{
          id : client.id      
        }
      });
  };

  editClient = async client =>{
    //edit the client.
    this.read = !this.read;
    console.log(this.read);
  };


  handleChange =() =>{
    //edit the client.

  };

  render() {

    const {data:{loading, clients}} = this.props;
    const addClient = false;
    const read = false;
    const styles = {
      margin: '1em 1em 1em 1em',
  }
  const styles2 = {
    margin: '1em 8em 1em 1em',
}
const styles3 = {
  display: 'flex',
  justifyContent: 'center',
  margin: '.5em'

}
    //{addClient ? <Form clients = {clients}></Form> : null}
    if (loading){
      return null;
    } 
    return (
      <div style={{display:'flex'}}> 
        <div style={{margin:"auto", width: auto}}>
          <Paper>
            <p style={styles3}>Clients </p>
            <List>
              {clients.map(client => (
                <ListItem key={`number ${client.id}`} role={undefined} style={styles} dense>
                  <span>
                  <Input required disableUnderline={true} inputProps={{ readOnly: this.read ? true:false}} label="Name" defaultValue= {client.name} onChange={this.handleChange} margin="dense" style={styles}/>
                  <Input required disableUnderline={true} inputProps={{ readOnly: this.read ? true:false}} label="Name" defaultValue= {client.number} onChange={this.handleChange} margin="dense" style={styles}/>
                  <Input required disableUnderline={true} inputProps={{ readOnly: this.read ? true:false}} label="Name" defaultValue= {client.email} onChange={this.handleChange} margin="dense" style={styles}/>
                  <Input required disableUnderline={true} inputProps={{ readOnly: this.read ? true:false}} label="Name" defaultValue="2018-12-31" label="Date of Birth" type="date" margin="dense" style={styles2}/>
                  </span>
                  <div>
                  <ListItemSecondaryAction>
                    <IconButton >
                      <EditIcon onClick ={()=> this.createClient({name: "HH",number:"HH",email:"HH",dob:"HH"})}/>
                      </IconButton>
                      <IconButton >
                      <DeleteIcon onClick ={()=> this.removeClient(client)}/>
                    </IconButton>

                  </ListItemSecondaryAction>
                  </div>
                </ListItem>   
              ))}
            </List>
            <p style={styles}>Add New Client</p>
            <Form clients = {clients}></Form>             
          </Paper>
        </div>
      </div>
    )
  }
}


export default compose(
graphql(editMutation, {name:"editText"}),
graphql(CreateMutation, {name:"createClient"}),
graphql(RemoveMutation, {name:"removeClient"}),
graphql(ClientQuery))
(App);
