import React from "react";
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';
import Input from '@material-ui/core/Input';
import { bool } from "prop-types";
import IconButton from '@material-ui/core/IconButton';
import EditIcon from '@material-ui/icons/Edit';
import DeleteIcon from '@material-ui/icons/Delete';
import ListItemSecondaryAction from '@material-ui/core/ListItemSecondaryAction';

class Table extends React.Component{

    state = { 
        text : ''
    };
    
    handleChange = (e) =>{
        const newText = e.target.value;
       
        this.setState({
            text : newText
        });
        console.log(this.state);

    };

    render(){
        const read = true; //sets readOnly property 
        const {client} = this.props;
        

        const styles = {
            margin: '1em 1em 1em 1em'
        }
        return(
            <div id="formWrapper">
                <ListItem key={`number ${client.id}`} role={undefined} dense>
                  <Input required disableUnderline={true} inputProps={{ readOnly: read ? true:false}} label="Name" defaultValue= {client.name} onChange={this.handleChange} margin="dense" style={styles}/>
                  <Input required disableUnderline={true} inputProps={{ readOnly: read ? true:false}} label="Name" defaultValue= {client.number} onChange={this.handleChange} margin="dense" style={styles}/>
                  <Input required disableUnderline={true} inputProps={{ readOnly: read ? true:false}} label="Name" defaultValue= {client.email} onChange={this.handleChange} margin="dense" style={styles}/>
                  <Input required disableUnderline={true} inputProps={{ readOnly: read ? true:false}} label="Name" defaultValue= {client.dob} onChange={this.handleChange} margin="dense" style={styles}/>
                  
                  <ListItemSecondaryAction>
                    <IconButton >
                      <EditIcon onClick ={()=> this.editClient(client)}/>
                      </IconButton>
                      <IconButton >
                      <DeleteIcon onClick ={()=> this.removeClient(client)}/>
                    </IconButton>
                </ListItemSecondaryAction>
                </ListItem>
            </div>
        )
    }
}
/*

*/

export default Table;
