import React, { Component } from 'react';
import './App.css';
import gql from "graphql-tag";
import { graphql,compose } from 'react-apollo';
import Paper from '@material-ui/core/Paper';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemSecondaryAction from '@material-ui/core/ListItemSecondaryAction';
import ListItemText from '@material-ui/core/ListItemText';
import Checkbox from '@material-ui/core/Checkbox';
import IconButton from '@material-ui/core/IconButton';
import DeleteIcon from '@material-ui/icons/Delete';
import Form from "./Form";
import { auto } from 'async';

const TodoQuery = gql`
{
  todos {
    id
    text
    complete
  }
}
`;

const UpdateMutation = gql`
mutation($id:ID!, $complete : Boolean!){
	updateTodo(id:$id, complete:$complete)
}
`;

const editText = gql`
mutation($id:ID!, $text : String!){
	editText(id:$id, text: $text)
}
`;

const RemoveMutation = gql`
mutation($id:ID!){
	removeTodo(id:$id)
}
`;


class App extends Component {

  updateTodo = async todo => {
    //update todo
    console.log(todo);
    await this.props.updateTodo({
      variables:{
        id : todo.id,
        complete: !todo.complete
      },
        update: store => {
        // Read the data from our cache for this query.
        //REDUX ACTION HERE TO CHANGE STATE
        //SWITCH TOGGLE

        const data = store.readQuery({ query: TodoQuery});
        data.todos = data.todos.map(
          x =>
            x.id === todo.id 
              ? todo = {
                ...todo,
                complete: !todo.complete
              }
           :x
          );
          console.log(todo);
        store.writeQuery({ query: TodoQuery, data });
      }
    });
  };

  removeTodo = async todo =>{   
      await this.props.removeTodo({
        variables:{
          id : todo.id      
        },
        update: store=>{
          const data = store.readQuery({query : TodoQuery});
          data.todos = data.todos.filter(x => x.id !== todo.id);
          store.writeQuery({query: TodoQuery, data});
        }
      });
  };

  render() {

    const {data:{loading, todos}} = this.props;

    if (loading){
      return null;
    } 
    return (
      <div style={{display:'flex'}}> 
        <div style={{margin:"auto", width: auto}}>
          <Paper>
            <Form ></Form>

             <List>
              {todos.map(todo => (
                <ListItem key={`number ${todo.id}`} role={undefined} dense button onClick={() => this.updateTodo(todo)}>
                  <Checkbox
                    checked={todo.complete}
                    tabIndex={-1}
                    disableRipple
                  />
                  <ListItemText primary={todo.text} />
                  <ListItemSecondaryAction>
                    <IconButton onClick ={()=> this.removeTodo(todo)}>
                      <DeleteIcon />
                    </IconButton>
                  </ListItemSecondaryAction>
                </ListItem>
              ))}
            </List> 

          </Paper>
        </div>
      </div>
    )
  }
}


export default compose(
graphql(UpdateMutation, {name:"updateTodo"}),
graphql(RemoveMutation, {name:"removeTodo"}),
graphql(TodoQuery))
(App);
