        }
    ]
};
//const store = createStore(() => [], {}, applyMiddleware(thunk));


function reducer(state=initialState, action){
    console.log("ACTION IS ",action);
    switch (action.type){

        case "ADD_CLIENT": {
            console.log("ADD CLIENT",state);
            return state;
        }
        case "REMOVE_CLIENT":{
            return state;
        }
        case "EDIT_CLIENT" : {
            return state;
        }
        default:
            return state;

    }
    
}

const store = createStore(reducer);
export default store;
