import React from "react";
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';
import Input from '@material-ui/core/Input';
import { bool } from "prop-types";

class Form extends React.Component{

    state = { 
        text : ''
    };
    
    handleChange = (e) =>{
        const newText = e.target.value;
       
        this.setState({
            text : newText
        });
        console.log(this.state);

    };

    render(){
        const read = true; //sets readOnly property 
        const {clients} = this.props;
        console.log(clients);

        const styles = {
            margin: '1em 1em 1em 1em'
        }
        return(
            <div id="formWrapper">
            





            <Input required inputProps={{ readOnly: read ? true:false}} label="Name" placeholder= "NAME"onChange={this.handleChange} margin="dense" style={styles}/>
            <Input required inputProps={{ readOnly: read ? true:false}} defaultValue="2018-12-31" label="Date of Birth" type="date" margin="dense" style={styles}/>
            <Input required inputProps={{ readOnly: read ? true:false}} label="Number" placeholder= "Number" onChange={this.handleChange} margin="dense" style={styles}/>
            <Input required inputProps={{ readOnly: read ? true:false}} label="Email" placeholder= "Email" onChange={this.handleChange} margin="dense" style={styles}/>

            <TextField required readOnly label="Name" onChange={this.handleChange} margin="normal" style={styles}/>
            <TextField required defaultValue="2018-12-31" label="Date of Birth" type="date" margin="normal" style={styles}/>
            <TextField required label="Number" margin="normal" style={styles}/>
            <TextField required label="Email" margin="normal" style={styles}/>
            <div style={{width: '100%',display:"flex", justifyContent:'flex-end'}}>
            <Button color="primary" aria-label="Add" >
        <       AddIcon />
            </Button>
            </div>
            </div>
        )
    }
}
/*

*/

export default Form;
