import React, { Component } from 'react';
import './App.css';
import gql from "graphql-tag";
import { graphql,compose } from 'react-apollo';
import Paper from '@material-ui/core/Paper';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemSecondaryAction from '@material-ui/core/ListItemSecondaryAction';
import ListItemText from '@material-ui/core/ListItemText';
import Checkbox from '@material-ui/core/Checkbox';
import IconButton from '@material-ui/core/IconButton';
import DeleteIcon from '@material-ui/icons/Delete';
import Form from "./Form";
import { auto } from 'async';

const TodoQuery = gql`
{
  todos {
    name
    number
    dob
    email
    id
    text
    complete
  }
}
`;

const UpdateMutation = gql`
mutation($id:ID!, $complete : Boolean!){
	updateTodo(id:$id, complete:$complete)
}
`;

const editMutation = gql`
mutation($id:ID!, $name: String!, $number: String!, $email : String!, $dob : String!, $text: String!){
  editText(id:$id,name:$name, number:$number, email:$email,dob:$dob,text:$text)
}
`;

const RemoveMutation = gql`
mutation($id:ID!){
	removeTodo(id:$id)
}
`;


class App extends Component {

  updateTodo = async todo => {
    //update todo
    console.log(todo);
    await this.props.updateTodo({
      variables:{
        id : todo.id,
        complete: !todo.complete
      },
    });
  };
/*
  editMutation = async todo => {
    //update todo
    console.log(todo);
    await this.props.editMutation({
      variables:{
        id : todo.id,
        complete: !todo.complete
      },
    });
  };
*/
  removeTodo = async todo =>{   
      await this.props.removeTodo({
        variables:{
          id : todo.id      
        }
      });
  };

  render() {

    const {data:{loading, clients}} = this.props;

    if (loading){
      return null;
    } 
    return (
      <div style={{display:'flex'}}> 
        <div style={{margin:"auto", width: auto}}>
          <Paper>
            <Form ></Form>{console.log(this.props)}



          </Paper>
        </div>
      </div>
    )
  }
}


export default compose(
graphql(UpdateMutation, {name:"updateTodo"}),
graphql(editMutation, {name:"editText"}),
graphql(RemoveMutation, {name:"removeTodo"}),
graphql(TodoQuery))
(App);
