import React, { Component } from 'react';
import './App.css';
import gql from "graphql-tag";
import { graphql,compose } from 'react-apollo';
import Paper from '@material-ui/core/Paper';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemSecondaryAction from '@material-ui/core/ListItemSecondaryAction';
import IconButton from '@material-ui/core/IconButton';
import EditIcon from '@material-ui/icons/Edit';
import DeleteIcon from '@material-ui/icons/Delete';
import Form from "./Form";
import store from "./store";
import { auto } from 'async';
import Input from '@material-ui/core/Input';
import { connect } from 'react-redux';
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';





const ClientQuery = gql`
{
  clients {
    name
    number
    dob
    email
    id
  }
}
`;

const editMutation = gql`
mutation($id:ID!, $name: String!, $number: String!, $email : String!, $dob : String!){
  editText(id:$id,name:$name, number:$number, email:$email,dob:$dob)
}
`;


const RemoveMutation = gql`
mutation($id:ID!){
	removeClient(id:$id)
}
`;

const CreateMutation  = gql`
mutation($name: String!, $number: String!, $email : String!, $dob : String!){
  createClient(name:$name, number:$number, email:$email, dob:$dob){
    name,
    number,
    email,
    dob,
    id
  }
}
`;
const loaded = false;

class App extends Component {

  constructor(props){
    super(props);
    //console.log("IN STATE",this.props.loadingdsa);
    this.state={
      editable:false
    }
  };

  createClient = async ({name, number, email, dob}) =>{

    //edit the client.

    await this.props.createClient({
      variables:{
        name,
        number,
        email,
        dob
      }
    })
    .then(
      console.log("CREATE CLIENT ",this.props)

    );


      //this.props.dispatch({type:"ADD_CLIENT", payload: this.p}); 





  };

  editText = async ({client,name2, number2, email2, dob2}) => {
    //update client
    console.log(client);
    this.setState({editable : !this.state.editable}); //ACTION TO EDIT
    await this.props.editText({
      variables:{
        id : client.id,
        name: name2,    
        number: number2,
        email: email2,
        dob: dob2
      },
    });
  };

  removeClient = async client =>{  

    console.log("REMOVED",client);
    this.props.dispatch({type:"REMOVE_CLIENT", payload: client}); 

      await this.props.removeClient({
        variables:{
          id : client.id      
        }
      });
  };

  handleChange =client =>{
    //edit the client. 

  };

  loadInitial = clients =>{
    //
    console.log("LADINIT",clients);

    clients.map(client => (this.props.dispatch({type:'ADD_CLIENT', payload: {name:client.name, number:client.number, email:client.email, dob:client.dob, id:client.id}})));

  }

  componentWillMount(){

  }
  render() {


    const {data:{loading, clients}} = this.props; //change to get from store

    const styles = {
      margin: '1em 1em 1em 1em',
  }
  const styles2 = {
    margin: '1em 8em 1em 1em',
}
const styles3 = {
  display: 'flex',
  justifyContent: 'center',
  margin: '.5em'

}
    if (loading){
      return null;
    } else if (!this.loaded){
      this.loadInitial(clients);
      this.loaded = true;
    }


  //console.log("this.props in APP is ", this.props);
  //console.log("this.state.clients in APP is ",this.state.clients);
 // console.log(store);


    return (


      <div style={{display:'flex'}}> 
        <div style={{margin:"auto", width: auto}}>
            {console.log(this)}
            <p style={styles3}>Clients </p>
            <List>
            {this.props.clients.map(client => (

                <ListItem key={client.id} role={undefined} style={styles} dense>
                  <span>
                  <Input required disableUnderline={this.state.editable} inputProps={{ readOnly: this.state.editable ? true:false}} label="Name" defaultValue= {client.name} onChange={this.handleChange} margin="dense" style={styles}/>
                  <Input required disableUnderline={this.state.editable} inputProps={{ readOnly: this.state.editable ? true:false}} label="Name" defaultValue= {client.number} onChange={this.handleChange} margin="dense" style={styles}/>
                  <Input required disableUnderline={this.state.editable} inputProps={{ readOnly: this.state.editable ? true:false}} label="Name" defaultValue= {client.email} onChange={this.handleChange} margin="dense" style={styles}/>
                  <Input required disableUnderline={this.state.editable} inputProps={{ readOnly: this.state.editable ? true:false}} label="Name" defaultValue="2018-12-31" label="Date of Birth" type="date" margin="dense" style={styles2}/>
                  </span>
                  <div>
                  <ListItemSecondaryAction>
                    <IconButton >
                      <EditIcon onClick ={()=> this.editText({client,name2:"EDITED",number2:"EDITED",email2:"EDITED",dob2:"GGGGGGGG"})}/>
                      </IconButton>
                      <IconButton >
                      <DeleteIcon onClick ={()=> this.removeClient(client)}/>
                      </IconButton >


                  </ListItemSecondaryAction>
                  </div>
                </ListItem>   
              ))}
            </List>
            <p style={styles}>Add New Client</p>

            <Form clients = {clients} createClient={this.createClient} ></Form>             

        </div>
      </div>

    )
  }
}


const mapStateToProps = state =>({
 clients: state.clients
})



export default compose(
graphql(editMutation, {name:"editText"}),
graphql(CreateMutation, {name:"createClient"}),
graphql(RemoveMutation, {name:"removeClient"}),
graphql(ClientQuery))(connect(mapStateToProps)(App));
