import React from "react";
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';

class Form extends React.Component{

    state = {
        text : ''
    };
    
    handleChange = (e) =>{
        const newText = e.target.value;
        this.setState({
            text : newText
        });

    };
    handleKeyDown = (e, type) => {
        console.log(type);
        
    }
    render(){
        const styles = {
            marginLeft: '1em', marginRight:'1em'
        }
        return(
            <div id="formWrapper">
            <TextField required label="Name" onChange={this.handleChange} onKeyDown={() => this.handleKeyDown("name")} margin="normal" style={styles}/>
            <TextField required defaultValue="2018-12-31" label="Date of Birth" type="date" margin="normal" style={styles}/>
            <TextField required label="Number" margin="normal" style={styles}/>
            <TextField required label="Email" margin="normal" style={styles}/>
            <div style={{width: '100%',display:"flex", justifyContent:'flex-end'}}>
            <Button color="primary" aria-label="Add" >
        <       AddIcon />
            </Button>
            </div>
            </div>
        )
    }
}
/*

*/

export default Form;
