import React, { Component } from 'react';
//import logo from './logo.svg';
import './App.css';
import gql from "graphql-tag";
import { graphql, compose } from 'react-apollo';
import Paper from '@material-ui/core/Paper';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemSecondaryAction from '@material-ui/core/ListItemSecondaryAction';
import ListItemText from '@material-ui/core/ListItemText';
import Checkbox from '@material-ui/core/Checkbox';
import IconButton from '@material-ui/core/IconButton';
import DeleteIcon from '@material-ui/icons/Delete';
//import CheckboxList from "./Compnents/CheckboxList";

const TodoQuery = gql`
{
  todos{
    id
    complete
    text
  }
}
`;

const UpdateMutation = gql`
mutation($id: ID!, $complete: BOOLEAN!){
	updateTodo(id: $id, complete: $complete)
}
`;


class App extends Component {

  updateTodo = todo => {

  };
  removeTodo = todo =>{

  };

  render() {
    console.log(this.props);
    const {data:{loading, todos}} = this.props;

    if (loading){
      return null;
    }
    return (
      <div style={{display: 'flex', justifyContent: 'center'}}>
        <div style = {{width: '50%'}}>

            <List >
            {todos.map(todo => (
            <ListItem  key={todo.id} role={undefined} dense button onClick={()=> this.updateTodo(todo)}>
              <Checkbox checked={todo.complete} tabIndex={-1} disableRipple/>
              <ListItemText style={{display: 'flex', justifyContent: 'center', fontSize:'200%'}} primary={todo.text} />

              <ListItemSecondaryAction>
                <IconButton onClick = {() => this.removeTodo(todo)}>
                  <DeleteIcon />
                </IconButton>
              </ListItemSecondaryAction>
            </ListItem>
              ))}
            </List>


         </div>       
       </div>
    )
  }
}



export default compose(
graphql(UpdateMutation),
  graphql(TodoQuery))
  (App);
